import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import {
  fetchTourById,
  increaseAdultValue,
  decreaseAdultValue,
  setAdultValue,
  increaseChildValue,
  decreaseChildValue,
  setChildValue,
  increaseInfantValue,
  decreaseInfantValue,
  setInfantValue
} from "../redux/tourSlice";

const BookingDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const { ticketData, adultValue, childValue, infantValue, loading, error } = useSelector(
    (state) => state.tour
  );

  useEffect(() => {
    dispatch(fetchTourById(id));
  }, [dispatch, id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!ticketData) return <p>No data available</p>;

  return (
    <div>
      <h1>{ticketData.title}</h1>
      <p>{ticketData.short_description}</p>

      <TicketCounter
        label="Adult (18+)"
        price={ticketData.adultPrice}
        value={adultValue}
        increase={() => dispatch(increaseAdultValue(1))}
        decrease={() => dispatch(decreaseAdultValue(1))}
        setValue={(val) => dispatch(setAdultValue(val))}
      />

      <TicketCounter
        label="Child (6–17)"
        price={ticketData.childPrice}
        value={childValue}
        increase={() => dispatch(increaseChildValue(1))}
        decrease={() => dispatch(decreaseChildValue(1))}
        setValue={(val) => dispatch(setChildValue(val))}
      />

      <TicketCounter
        label="Infant (0–5)"
        price={ticketData.infant} 
        value={infantValue}
        increase={() => dispatch(increaseInfantValue(1))}
        decrease={() => dispatch(decreaseInfantValue(1))}
        setValue={(val) => dispatch(setInfantValue(val))}
      />
    </div>
  );
};

export default BookingDetails;

const TicketCounter = ({ label, price, value, increase, decrease, setValue, info, freeLabel }) => (
  <section className="tickets_order">
    <ul>
      <h2>{label}</h2>
      {info && <li>{info}</li>}
      <h3 style={{ color: "#FA8B02" }}>
        €{price !== null && price !== undefined ? price : freeLabel || "Not available"}
      </h3>
    </ul>
    <section className="counter">
      <button onClick={decrease} disabled={value <= 0}>
        -
      </button>
      <input
        type="number"
        min={0}
        value={value}
        onChange={(e) => {
          const val = Math.max(0, Number(e.target.value));
          setValue(val);
        }}
      />
      <button onClick={increase}>+</button>
    </section>
  </section>
);
