import React, { useState, useEffect } from 'react';
// import { useParams } from 'react-router-dom';
import tours from '../DB/Tuscany.json';
import { ToursContext } from '../context/ToursContext';

export default function SpecificTour() {
//   const { id } = useParams(); // Access id from URL
  const [tour, setTours] = useState(ToursContext);

  useEffect((id) => {
    const foundTour = tours.find(tour => tour.id === id); 
    setTours(foundTour);
  }, [setTours]);

  if (!tour) return <p>Loading...</p>;

  return (
    <section>
      {tours.find(tour => (
        <section className="tour" key={tour.id}>
          <h2>{tour.title}</h2>
          <p>{tour.description}</p>
        </section>
      ))}
    </section>
  );
}
