import React from "react";
import next from '../assets/home/icons/test_right.svg';
import prev from '../assets/home/icons/test_left.svg';
import { useSelector, useDispatch } from "react-redux";
import { setDate,selectedDate } from "../slices/dateSlice";

export default function DatePicker() {
  const currentMonth = new Date().getMonth();
  const currentYear = new Date().getFullYear();
  const daysOfMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

  const weekdays = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
  const months = [
    "January", "February", "March", "April", "May", "June", "July", "August", "September", "October",
    "November", "December"
  ];

  const daysArray = [...Array(daysOfMonth).keys()].map((day) => {
    const choosedDate = new Date(currentYear, currentMonth, day + 1);
    return { day: day + 1, fullDate: choosedDate.toDateString() };
  });

  const dispatch = useDispatch();
  const selectedDate = useSelector((state) => state.datepicker.selectedDate);
  
  const handleNextMonth = new Date(currentMonth + 1);
  const handlePrevMonth = new Date(currentMonth - 1);

  return (
    <section>
      <section className="calendar">
        <section className="calendar-header">
          <h3>{months[currentMonth]} {currentYear}</h3>
          <section >   
            
   <button  onClick={handlePrevMonth}>
   <img src={prev} alt="Previous Month" />
   </button>
           <button onClick={handleNextMonth} >
              <img src={next} alt="Next Month" />
              </button>
      
        </section>
        </section>
        <section className="calendar-subheader">
          {weekdays.map((day, index) => (
            <h3 key={index}>{day}</h3>
          ))}
        </section>
        <section className="calendar-days">
          {daysArray.map(({ day, fullDate }) => (
            <span
              key={`${currentYear}-${currentMonth + 1}-${day}`} 
              style={{
                padding: "0.5em",
                border: "0.5em white ",
                cursor: "pointer",
                background: selectedDate === fullDate ? "#FA8B02" : "white",
              }}
              onClick={() => dispatch(setDate(fullDate))}
            >
              {day}
            </span>
          ))}
        </section>
      </section>
      {/* <h3>Selected Date: {selectedDate || "None"}</h3> */}
    </section>
  );
}
