import { createSlice } from "@reduxjs/toolkit";
import Tuscany from '../DB/Tuscany.json'

const tourSlice = createSlice({
  name:'tour',
  initialState:{
    adultPrice :0,
    childPrice :0,
    infantPrice:0,
    adultCost:0,
    childCost:0,
    infantCost:0,
    totalCost:0,
    value:0,
  },
  reducers:{
    value:(state,action) => {
      state.value = action.payload;
    },
    increase: (state) => {
      state.value += 1;
  },
  descrease: (state) => {
      if (state.value > 0) state.value -= 1;
  },
  adultCost: (state, action) => {
      state.adultPrice = action.price;
      state.adultCost = action.quantity * state.adultPrice;
  },
  childCost: (state, action) => {
      state.childPrice = action.price;
      state.childCost = action.payload.quantity * state.childPrice;
  },
  infantCost: (state, action) => {
      state.infantPrice = action.payload.price;
      state.infantCost = action.payload.quantity * state.infantPrice;
  },
  totalCost: (state,) => {
      state.totalCost = state.adultCost + state.childCost + state.infantCost;
  },

  }
});

export const { 
  setValue,
  setIncrease,
  setDescrease,
  setAdultCost,
  setChildCost,
  setInfantCost,
  setTotalCost,
} = tourSlice.actions
export default tourSlice.reducer;
