import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import {
  fetchTourById,
  fetchTours
 
} from "../../slices/tourSlice";

const TourDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const tours = useSelector((state) => state.tour.tours);
  const ticketData = useSelector((state) => state.tour.ticketData);

  useEffect(() => {
    if (tours.length === 0) {
      dispatch(fetchTours());
    }
  }, [dispatch, tours.length]);

useEffect(() => {
  if (id) {
    dispatch(fetchTourById(id));
  }
}, [dispatch, id]);

  if (!ticketData.title) {
    return <div>Loading tour details...</div>;
  }

  return (
    <div>
      <h1>{ticketData.title}</h1>
      <img src={ticketData.img} alt={ticketData.title} />
      <p>Adult Price: {ticketData.adultPrice}</p>
      <p>Child Price: {ticketData.childPrice}</p>
      <p>Infant Price: {ticketData.infantPrice}</p>
      {/* інші деталі туру */}
    </div>
  );
};

export default TourDetails;
