import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

export const MainGallery = () => {
  const { id } = useParams();
  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#main-gallery", 
      children: "a",
      showHideAnimationType: 'zoom',
      pswpModule: () => import("photoswipe"),
    });

    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0", className: "main-img", area: "main" },
    { id: 1, src: gallery.img1, alt: "Gallery place 1", className:"galleryImg" },
    { id: 2, src: gallery.img2, alt: "Gallery place 2",  className:"galleryImg"},
    { id: 3, src: gallery.img3, alt: "Gallery place 3",  className:"galleryImg" }
  ];
  


  return (
    <section className="main_gallery" id="main-gallery">
      {/* {images.map((img) => (
        <a
          key={img.id}
          href={img.src}
          data-pswp-width={img.width || 800}
          data-pswp-height={img.height || 600}
          target="_blank"
          rel="noreferrer"
        >
          <section className="main_gallery">
            <img
              src={img.src}
              alt={img.alt}
              className={img.className || ""}
            />
            
            </section>
          
        </a>
      ))} */}
    
        <img src={gallery.img} style={{height:'600px',width:'700px'}}/>
        <section>
            <img src={gallery.img1}/>
      <img src={gallery.img2}/>
      <img src={gallery.img3}/>
        </section>
    
      </section>
      


  
  );
  
};
