import React, {  useState } from "react";
import {Link} from 'react-router-dom';
import arrow from '../../assets/home/icons/CheckIn/arrow-left.svg'
import Login from "./Login";
import reset from '../../assets/home/icons/CheckIn/resetPassword.svg';
import ApprovedPassword from './ApprovedPassword';
import { useDispatch } from "react-redux";
import { setNewPassword } from "../../slices/authSlice";



export  default function NewPassword () {

const [password,setPassword] = useState('');
const [confirmPassword,setConfirmPassword] = useState('');
const [error,setError] = useState("");

axios.post('http://localhost:3000/auth/newPassword')
    .then(res => console.log('Password was successfully updated'))
    .catch(res => console.error('Invali data'))


const dispatch = useDispatch();

const handleSubmit = async(e) => {

    e.preventDefault();

  if(password !== confirmPassword) {
    setError("Passwords do not match");
    return;
  }
  dispatch(setNewPassword(password));
}

    return(
        <section className="auth">
            <form 
            onSubmit={handleSubmit}
            method="post" action="/auth/newPassword">
            <img className="form_img" src={reset} alt="enter new password"/>
            <h1>Set New Password</h1>
            <p>Your new password must be different from previously used passwords.</p>
            
                <label for="new_password">New Password</label>
                <input
                value={password}
                    name="new_password"
                    type="password" 
                    onChange={(e)=> setPassword(e.target.value)}
                    placeholder="Enter your password"
                />
                <label for="confirm_password">Confirm Password</label>
                <input 
                value={confirmPassword}
                    name="confirm_password"
                    type="password" 
                    onChange={(e)=> setConfirmPassword(e.target.value)}
                    placeholder="Enter your password"
                />

                <Link to="/auth/newPassword/approvedPassword" element={<ApprovedPassword/>}>
                
                <button 
                className="general_btn" 
                type="submit"> Reset Password</button>
                </Link>
                        {error && <p style={{ color: "red" }}>{error}</p>}

                <section className="additionals">
                <Link to="/auth/login" element={<Login/>}>
                    <button>
                        <img src={arrow} alt="back to login"/> Back to Login
                    </button>
                </Link>
            </section>
            </form> 
        </section>
    )
}
