import React, { useContext, useEffect, useState } from "react";
import { useBook } from "../../context/BookContext";

// import ProgressBar from "../../components/ProgressBar";
export default function TicketOverview(...args: []) {

    const { ticketItems } = useBook();

    useEffect(() => {
        localStorage.setItem("ticketItem", JSON.stringify(ticketItems));
    }, [ticketItems]);
    return (
        <>
            <section className="tickets_overview">
                <h2>Your ticket Overview</h2>
                <section>
                    {/* {/* <img src={Tuscany.image} alt={Tuscany.title}/> */}
                    <section>
                        <h2>{Tuscany.title$}</h2>
                        <p>{ticketItems.date}</p>
                        <p>{ticketItems.time}</p>
                    </section>
                </section>
                <hr />
                <section className="quanity">
                    <section>
                        {/* <label>{adult.quantity}</label>
    <p>Adult(18+){adult.amount}</p> */}
                    </section>
                    <section>
                        {/* <label>{child.quantity}</label>
    <p>Child(6-17){child.amount}</p> */}
                    </section>
                    <section>
                        {/* <label>{infant.quantity}</label>
    <p>Infant(0-5){infant.amount}</p> */}
                    </section>
                </section>
                <hr />
                <section className="tckt_amount">
                    {/* <p>{adult.amount}</p>
        <p>{child.amount}</p>
        <p>{infant.amount}</p> */}
                </section>
                <section>
                    <h2>Total Price</h2>
                    {/* <p>â‚¬{totalAmount}</p> */}
                </section>
                <Link to="/user" element={<UserDetails />}>

                    <button className="general_btn">Go to the Next Step</button>
                </Link>
            </section>
        </>
    );
}
