import React from "react";
import ProgressBar from '../../components/ProgressBar';
import data from '../../assets/home/icons/Tours/date.svg';
import time from '../../assets/home/icons/Tours/time.svg';
import Tuscany from '../../DB/Tuscany.json';
import {setTime,setDate,adultPrice,childPrice,infantPrice, adultCost,childCost,infantCost, decrease,increase,value} from '../../slices/tourSlice';
import { useDispatchбг } from "react-redux";



export default function BookingDetails () {
	const dispatch = useDispatch();
	const selectedTime = useDispatch((state)=> state.datepicker.setTime);
	const selectedDate = useDispatch((state)=> state.datepicker.setDate);

	const adultPrice = useDispatch((state)=> state.tour.adultPrice);
	const childPrice = useDispatch((state)=> state.tour.childPrice);
	const infantPrice = useDispatch((state)=> state.tour.infantPrice);

	const adultCost = useDispatch((state)=> state.tour.adultCost );
	const childCost = useDispatch((state)=> state.tour.childCost);
	const infantCost = useDispatch((state)=> state.tour.infantCost);

	const setDescrease = useDispatch((state)=> state.tour.decrease);
	const setIncrease = useDispatch((state)=> state.tour.increase);
	const setValue = useDispatch((state)=> state.tour.setValue);

    return(
<section className="booking_container">
	{/* <ProgressBar/> */}
  	<section className="booking_main">

      <section className="booking_data" >
        <h2>When you will visit?</h2> 
				<section>             
					<img src={data} alt="choose a data"/>	<select> 
							<option>
								{selectedDate || 'Choose a date'}
							</option>
						</select>
					</section> 
      </section>
    
      <section className="booking_data" >
        <h2>Which time?</h2>
				<section>
					<img src={time} alt="choose a time"/>	<select>  
							<option>
								{selectedTime || 'Choose a time'}
							</option>
						</select>
				</section>
        
      </section> 

			<section style={{ backgroundColor: "#EFEFEF", opacity: 0.4 }}>
			<ul >
  			<h2>Select Your Tickets</h2>
  				<li>Free for kids under 6 and disabled visitors (74%+)</li>
  				<li>Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue</li>
			</ul>	
			</section>

			<section className="tickets_order">
				<ul>
					<h2>Adult(18+)</h2>
					{/* <h3>{specificTour.adultPrice?? "Not available"}</h3> */}
				</ul>

				<section className="tickets_counter">
					<button  
						onClick={() => dispatch(setDescrease())}> - </button>

					<input
						type="number"
						value={value} 
						onChange={(e)=> setValue(e.target.value)}
					/>
					<button onClick={()=> dispatch(setIncrease())}> + </button>
						<h6>Total Adult Cost: {adultCost}</h6> 
				</section> 
			</section>

			<section className="tickets_order">
					<ul >
						<h2>Child (6-17)</h2>
							<li>With valid ID</li>
							<li>Only in combination with: Adult (18+)</li>
					</ul>

				<section className="tickets_body">

					<section className="tickets_order">
							<ul>
								{/* <h3>{specificTour.childPrice ?? "Not available"}</h3> */}
							</ul>
						<section className="tickets_counter">
							 <button  
							onClick={() => dispatch(setDescrease())}> - </button>
								<input
									type="number"
									value={value} 
									onChange={(e)=> setValue(e.target.value)}
								/>
							<button onClick={()=> dispatch(setIncrease())}> + </button>
							<h6>Total Adult Cost: {childCost}</h6> 
						</section>
									
						<section className="tickets_order">
							<ul>
								<h2>Infant (0-5) </h2>
								<li>Only in combination with: Adult (18+)</li>
							</ul>
							<section className="tickets_counter">
								 <button  
									onClick={() => dispatch(setDescrease())}>-</button>
								<input
									type="number"
									value={value} 
									// onChange={handleChange}
								/>
								<button onClick={()=> dispatch(setIncrease())}>+</button>
								<h6>Total Adult Cost: {infantCost}</h6> 
							</section>
						</section>

					</section> 

				</section>
       
      </section>
		</section>

</section>

    )
}
