import React, { useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import Dropdown from 'react-dropdown';
import logo from '../assets/home/logo.svg';
import Modal from './modal';
import Home from "../pages/home";
import AboutUs from "../pages/About_Us";
import ContactUs from "../pages/Contact_Us";
import Tours from '../pages/Tours';
import CreateAccount from '../pages/auth/CreateAccount';

export default function Header() {
    // const [isModalOpen, setIsModalOpen] = useState(false);
    const languageOptions = ['ENG', 'UKR'];

    // const handleLanguageChange = (option) => {
    //     console.log(option.value); // Обробка зміни мови
    // };

    // const handleOpenModal = useCallback(() => {
    //     setIsModalOpen(true);
    // }, []);

    // const handleCloseModal = useCallback(() => {
    //     setIsModalOpen(false);
    // }, []);

    return (
        <header>
            <Link to="/" element={<Home />}>
                <img className="logo" src={logo} alt="Tours to Tuscany" />
            </Link>

            <nav className="setUp">
                
                    <li>
                        <Link to="/" element={<Home />}>Home</Link>
                    </li>
                    <li>
                        <Link to="/aboutUs" element={<AboutUs />}>About us</Link>
                    </li>
                    <li>
                        <Link to="/Tours" element={<Tours />}>Tours</Link>
                    </li>
                    <li>
                        <Link to="/contact" element={<ContactUs />}>Contact Us</Link>
                    </li>
                
            </nav>

            <section className="options">
                <Dropdown
                    options={languageOptions}
                    onChange={handleLanguageChange}
                    placeholder="Select Language"
                />
                <Link to='/auth/createAccount' element={<CreateAccount/>}>
                 <button className="general_btn" onClick={handleOpenModal}>
                    Sign Up
                </button>
                </Link>
               
            </section>

            {/* Рендеринг модального вікна */}
            {/* <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
                <CreateAccount />
            </Modal> */}
        </header>
    );
}
