import { fetchTourById } from "../../slices/tourById";

export default function BookingDetails() {
  const { id } = useParams();
  
  const dispatch = useDispatch();
  const { ticketData, loading, error } = useSelector((state) => state.tour);


 useEffect(() => {
  if (id) {
    dispatch(fetchTourById(id))
