import React, {  useEffect, useState } from "react";
import {useDispatch,useSelector} from 'react-redux';
import {Link, useParams } from 'react-router-dom'
import ProgressBar from "../../components/ProgressBar";
import tours from '../../DB/Tuscany.json';
import { setIncrease,setDescrease,setAdultPrice } from "../../slices/counterSlice";
import { setTours,setTour } from "../../slices/tourSlice";
import UserDetails from '../Purchase/UserDetails';

export default function BookingDetails () {

    const {id} = useParams();

    const dispatch = useDispatch();

    const allTours = useSelector((state) => state.tour.tours); // Отримуємо з Redux
    
    console.log("Тури у Redux:", allTours);
        
    const adultCost = useSelector((state) => state.counter.adultCost);
    const childCost = useSelector((state) => state.counter.childCost);
    const infantCost = useSelector((state) => state.counter.infantCost);

    const selectedDay = useSelector((state) => state.tour.setDate);
    const selectedTime = useSelector((state) => state.tour.setTime);
    const specificTour = useSelector((state) => state.tour.specificTour);

    const [value, setValue] = useState(0);

 
    const handleChange = (e) =>{
     setValue(e.target.value);
    };
 
    return (
      <>
    
  <section className="booking_container">
    <ProgressBar/>
      <section className="booking_main">
        {/* Date*/}
          <section className="booking_details" >
            <h2>When you will visit?</h2>
              <select>
                <option>
                  {selectedDay || "Choose a day"}
                </option>
              </select>
          </section>
        {/*Time */}
            <section className="booking_details" >
                <label><h2>Which time?</h2></label>
                  <select>
                      <option>
                        {selectedTime || "choose the time" }
                      </option>
                    </select>
            </section>
        {/*Tickets */}
        <ul>
          <h2>Select Your Tickets</h2>
            <li>Free for kids under 6 and disabled visitors (74%+) </li>
            <li>Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue</li>
        </ul>
    <section className="tickets_order">
      <ul>
        <h2>Adult(18+)</h2>
        <h3>{specificTour?.adultPrice || 0}</h3>
      </ul>
      <section className="tickets_counter">
        <button  
          onClick={() => dispatch(setDescrease())}> - </button>
        <input
          type="number"
          value={value} 
          onChange={handleChange}
        />
        <button onClick={()=> dispatch(setIncrease())}> + </button>
          <h6>Total Adult Cost: {adultCost}</h6>
      </section> 

    </section>

      <section className="tickets_order">
        <ul >
          <h2>Child (6-17)</h2>
            <li>With valid ID</li>
            <li>Only in combination with: Adult (18+)</li>
        </ul>
          <section className="tickets_order">
            <ul>
              <h2>Adult(18+)</h2>
              <h3>{specificTour?.childPrice ?? "Not available"}</h3>
            </ul>
          <section className="tickets_counter">
            <button  
            onClick={() => dispatch(setDescrease())}> - </button>
              <input
                type="number"
                value={value} 
                onChange={handleChange}
              />
            <button onClick={()=> dispatch(setIncrease())}> + </button>
            <h6>Total Adult Cost: {childCost}</h6>
          </section>
                          
          <section className="tickets_order">
            <ul>
              <h2>Infant (0-5) </h2>
              <li>Only in combination with: Adult (18+)</li>
            </ul>
            <section className="tickets_counter">
              <button  
                onClick={() => dispatch(setDescrease())}>-</button>
              <input
                type="number"
                value={value} 
                onChange={handleChange}
              />
              <button onClick={()=> dispatch(setIncrease())}>+</button>
              <h6>Total Adult Cost: {infantCost}</h6>
            </section>
          </section> 
          </section> 
       
      </section>

      </section>
  </section>
  </>
    );
}

