import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
// import { useParams } from "react-router-dom";
import { fetchTourById } from "../../slices/tourById";
import ProgressBar from '../../components/ProgressBar';
import TicketOverview from '../../components/TicketOverview';
import { Icon } from "@iconify/react";

import { 
  setAdultValue,
  increaseAdultValue,
  descreaseAdultValue,

  setChildValue,
  increaseChildValue,
  decreaseChildValue,

  setInfantValue,
  increaseInfantValue,
  descreaseInfantValue,
} from "../../slices/tourById";

import { selectedTime,selectedDate } from "../../slices/dateSlice";


export default function BookingDetails() {
  const { id } = useParams();

  const dispatch = useDispatch();
  const { ticketData, loading, error } = useSelector((state) => state.tour);

  const adultValue = useSelector(state => state.tour.adultValue);
const childValue = useSelector(state => state.tour.childValue);
const infantValue = useSelector(state => state.tour.infantValue);

  useEffect(() => {
    if (id) {
      dispatch(fetchTourById(id))
        .unwrap()
        .then((data) => console.log("‚úÖ Tour fetched:", data))
        .catch((err) => console.error("‚ùå Tour fetch failed:", err));
      console.log("üßæ ticketData:", ticketData);

    }
  }, [dispatch, id]);



  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string"
          ? error
          : error.message || JSON.stringify(error)}
      </p>
    );

    
  if (!ticketData) return <p>No data</p>;


  return (

    <>
      {/* <h2>{ticketData.adultPrice}</h2> */}
      {/* <img>{ticketData.img}</img> */}
      <>
        <ProgressBar />
        <section className="booking_container">
          <section>
            <section className="booking_data">
              <h2>When you will visit?</h2>
              <section style={{ display: "flex", alignItems: "center" }}>
                {/* <img src={dateIcon} alt="choose a date" /> */}
                <Icon icon="system-uicons:calendar-month" width={24} height={24}/>
                <select>
                  <option>{selectedDate || "Choose a date"}</option>
                </select>
              </section>
              {/* {!selectedDate && <DatePicker />}  */}
            </section>
            <section className="booking_data">             <h2>Which time?</h2>
              <section style={{ display: "flex", alignItems: "center" }}>
                <Icon icon="system-uicons:clock"  height={24} width={24} />
                {/* <img src={timeIcon} alt="choose a time" /> */}
                <select>
                  <option>{selectedTime || "Choose a time"}</option>
                </select>
              </section>
            </section>

            <ul>
              <h2>Select Your Tickets</h2>
              <li>Free for kids under 6 and disabled visitors (74%+)</li>
              <li>
                Pregnant women, families with strollers, and visitors on crutches
                can buy priority tickets at the venue
              </li>
            </ul>

            <section
              key={ticketData.id}
              className="tickets_body">
              {/* Adult */}
              <TicketCounter
                label="Adult (18+)"
                price={ticketData.adultPrice}
                value={adultValue}
                increase={() => dispatch(increaseAdultValue(1))}
                decrease={() => dispatch(descreaseAdultValue(1))}
                setValue={(val) => dispatch(setAdultValue(val))}
              />
              {/* Child */}
              <TicketCounter
                label="Child (6-17)"
                price={ticketData.childPrice}
                value={childValue}
                increase={() => dispatch(increaseChildValue(1))}
                decrease={() => dispatch(decreaseChildValue(1))}
                setValue={(val) => dispatch(setChildValue(val))}
                info="With valid ID. Only in combination with: Adult (18+)"
              />
              {/* Infant */}
              <TicketCounter
                label="Infant (0-5)"
                price={ticketData.infantPrice}
                value={infantValue}
                increase={() => dispatch( increaseInfantValue(1))}
                decrease={() => dispatch( descreaseInfantValue(1))}
                setValue={(val) => dispatch( setInfantValue(val))}
                info="Only in combination with: Adult (18+)"
                freeLabel="FREE"
              />
            </section>
          </section>
          <TicketOverview nextLink="/User" tourId={id} />
        </section>
      </>

    </>
  );
}

const TicketCounter = ({ label, price, value, increase, decrease, setValue, info, freeLabel }) => (
  <section className="tickets_order">
    <ul>
      <h2>{label}</h2>
      {info && <li>{info}</li>}
      <h3 style={{ color: "#FA8B02" }}>
        ‚Ç¨{price !== null && price !== undefined ? price : freeLabel || "Not available"}
      </h3>
    </ul>
    <section className="counter">
      <button onClick={decrease} disabled={value <= 0}>
        -
      </button>
      <input
        type="number"
        min={0}
        value={value}
        onChange={(e) => {
          const val = Math.max(0, Number(e.target.value));
          setValue(val);
        }}
      />
      <button onClick={increase}>+</button>
    </section>
  </section>
)
