import React, { useState } from "react";
import testimotionalData from '../DB/Tuscany.json';
import back from '../assets/home/icons/test_left.svg'
import right from '../assets/home/icons/test_right.svg'
import { useParams } from "react-router-dom";

export default function Testimotionals (){

const [currentSlide,setCurrentSlide] = useState(0);


const handleNextSlide = () => {
setCurrentSlide(currentSlide + 1)};
const handlePrevSlide = () => {
setCurrentSlide(currentSlide - 1)
}
const {id} = useParams();

// const testimonial = testimotionalData[currentSlide];
const testimonial = testimotionalData.find((testimonial) => testimonial.id === parseInt(id))
    return(
       <section >
        {/* <section  className="btns">
          <button className="back" onClick={handlePrevSlide}><img src={back}/></button>
          <button className="next" onClick={handleNextSlide}><img src={right}/> </button>
        </section>
        <h1><strong>Happy Customers Says</strong></h1> */}
        
       
        {testimonial &&( 
          <section
            key={testimonial.id}
            className="testimotionals"
            >
              <section className="testimon_viewport">

                <section className="testimonial_container">
                  <h3 className="name">{testimonial.name1}</h3>
                  <p className="review">{testimonial.review1}</p>
                </section>

                <section className="testimonial_container" >
                  <h3 className="name">{testimonial.name2}</h3>
                  <p className="review">{testimonial.review2}</p>
                </section>

                <section  className="testimonial_container">
                  <h3 className="name">{testimonial.name3}</h3>
                  <p className="review">{testimonial.review3}</p>
                </section>
                <section  className="testimonial_container">
                  <h3 className="name">{testimonial.name4}</h3>
                  <p className="review">{testimonial.review4}</p>
                </section>
          </section>
           </section>
        )}
        
      
        
       </section>
    )
}
