import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { selectedDate } from "./dateSlice";

import tours from './fetchTours';
export const fetchTours = createAsyncThunk("tour/fetchTours", async () => {
  const response = await axios.get("http://localhost:5000/tour/tours/:{id}");
  return response.data;
});

const tourSlice = createSlice({
  name: "tour",
  initialState: {
    tours:[],
    ticketData: {
      adultValue: 0,
      childValue: 0,
      infantValue: 0,
      adultPrice: null,
      childPrice:  null,
      infantPrice:  0,
      adultCost: 0,
      totalPrice: 0,
      img: null,
      title: null,
      numberOfGroup: null,
      duration:null,
      guide:null,
      language:null,
      fees:null,
      transport:null,
      payment: null,
      statusTour: null,
    },
  },
  
  reducers: {
    setAdultValue: (state, action) => {
      state.ticketData.adultValue = action.payload;
    },
 setAdultPrice: (state, action) => {
  const { id } = action.payload;
  const tour = state.tours.find((item) => item.id === id);
  state.ticketData.adultPrice = tour ? tour.adultPrice : null;
},


    // const { id } = action.payload;
      // const tour = Tuscany.find((item) => item.id === id);
      // state.adultPrice = tour ? tour.adultPrice : null;
    setAdultCost: (state) => {
      state.ticketData.adultCost = state.ticketData.adultPrice * state.ticketData.adultValue;
    },
    setChildValue: (state, action) => {
      state.ticketData.childValue = action.payload;
    },
    setInfantValue: (state, action) => {
      state.ticketData.infantValue = action.payload;
    },
   setChildPrice: (state, action) => {
  const { id, tours = [] } = action.payload;
  const tour = tours.find((item) => item.id === id);
  state.ticketData.childPrice = tour ? tour.childPrice : null;
},

    setInfantPrice: (state, action) => {
      const { id } = action.payload;
      const tour = tours.find((item) => item.id === id);
      state.ticketData.infantPrice = tour ? tour.infantPrice : 0;
    },
    setChildCost: (state) => {
      state.ticketData.childCost = state.ticketData.childValue * state.ticketData.childPrice;
    },
    setInfantCost: (state) => {
      state.ticketData.infantCost = state.ticketData.infantValue * state.ticketData.infantPrice;
    },
    setTotalPrice: (state) => {
      state.ticketData.totalPrice = state.ticketData.adultCost + state.ticketData.childCost + state.ticketData.infantCost;
    },
    increaseAdultValue: (state) => {
      state.ticketData.adultValue += 1;
    },
    decreaseAdultValue: (state) => {
      if (state.ticketData.adultValue > 0) state.ticketData.adultValue -= 1;
    },
    increaseChildValue: (state) => {
      state.ticketData.childValue += 1;
    },
    decreaseChildValue: (state) => {
      if (state.ticketData.childValue > 0) state.ticketData.childValue -= 1;
    },
    increaseInfantValue: (state) => {
      state.ticketData.infantValue += 1;
    },
    decreaseInfantValue: (state) => {
      if (state.ticketData.infantValue > 0) state.ticketData.infantValue -= 1;
    },
    setImgTour: (state, action) => {
      const tour = tours.find((tour) => tour.id === action.payload);
      state.ticketData.img = tour.img || null;
    },
    setTourTitle: (state, action) => {
      const tour = tours.find((tour) => tour.id === action.payload);
      state.ticketData.title = tour.title || null;
    },
    setNumberOfGroup: (state, action) => {
      const groups = tours.find((people) => people.id === action.payload);
      state.ticketData.numberOfGroup = groups.numberOfGroup || null;
    },
    setTourDuration: (state, action) => {
      const durationTour = tours.find((tour) => tour.id === action.payload);
      state.ticketData.duration = durationTour.duration || null;
    },
    setGuide: (state, action) => {
      const guideInclude = tours.find((guide) => guide.id === action.payload);
      state.ticketData.guide = guideInclude.guide || null;
    },
    setLanguage: (state, action) => {
      const languageTour = tours.find(
        (language) => language.id === action.payload
      );
      state.ticketData.language = languageTour.language || null;
    },
    setTourFees: (state, action) => {
      const fees = tours.find((tourFees) => tourFees.id === action.payload);
      state.ticketData.fees = fees.fees || null;
    },
    setTourTransport: (state, action) => {
      state.ticketData.transport = action.payload || null;
    },
    setPaymentMethod: (state, action) => {
      state.ticketData.payment = action.payload;
    },
    setStatusTour: (state, action) => {
      const selectedDate = new Date(action.payload);
      const today = new Date();

      if (selectedDate > today) {
        state.ticketData.statusTour = "upcoming";
      } else {
        state.ticketData.statusTour = "ended";
      }
    },
    setTicketData: (state, action) => {
      state.ticketData = (action.payload);
    },
  },
});

export const {
  setTourTitle,
  setAdultValue,
  setAdultPrice,
  setAdultCost,
  setChildValue,
  setChildPrice,
  setChildCost,
  setInfantValue,
  setInfantPrice,
  setInfantCost,
  setTotalPrice,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,
  setImgTour,
  setNumberOfGroup,
  setTourDuration,
  setLanguage,
  setTourFees,
  setTourTransport,
  setPaymentMethod,
  setStatusTour,
  setTicketData,
} = tourSlice.actions;
export default tourSlice.reducer;
