import React from "react";
import Header from "../components/header";
import Statistic from '../components/statistic';
import Footer from '../components/footer';
import Lucca from '../assets/home/Lucca.png'
import '../components/advantages';
import Advantages from "../components/advantages";
import Testimotionals from "../components/Carousel/TestimonialsCarousel";
import { Link } from "react-router-dom";
import Tours from "./Tours";

export default function AboutUs() {

    return(
    <>
      <section className="header_aboutUs">

        <section className="intro_aboutUs">
          <p>Our team cares about full relax</p>
          <h2><strong>Unwind and explore! We curate quality travel tours that handle everything, leaving you free to simply relax and discover new destinations.</strong></h2>
         <Link to='/tours' element={<Tours/>}>
        
          <button >View our Tour Packages</button>
        </Link>
        </section>

      </section>
      <section className="descrip_aboutUs">
        <img src={Lucca} alt="Center of Lucca"/>
            <section className="aboutUs">
                  <h4>welcome to our site!</h4>
                  <h1>We Are The Center Of Lucca To Offer You The Best </h1>
                  <h3>We are right in the center of Lucca to offer you the real city life! With years of experience in practically every tourism sector, with us you can find complete packages at the lowest price, to travel and learn and have fun all without worries and without stress. What are you waiting for, book a bright evening, a trip to beautiful Tuscany or a personal tour for you!
                  </h3>
                <Statistic/>
            </section>
            
      </section>
          <Advantages/>
          <Testimotionals/>
    </>
    )
}
