import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Icon } from "@iconify/react/dist/iconify.js";

// import dateIcon from "../assets/home/icons/Tours/date.svg";
// import timeIcon from "../assets/home/icons/Tours/time.svg";


import { 
   setAdultValue,


  setChildValue,
 
 
  setInfantValue,
  
  


  setTotalPrice, setTotalAdultPrice, setTotalChildPrice,
  setTotalInfantPrice, } from "../slices/tourById";

export default function TicketOverview({ nextLink, onNext,tourId }) {

  const dispatch = useDispatch();
  const navigate = useNavigate();
useEffect(() => {
    if (tourId) {
      dispatch(fetchTourById(tourId));
    }
  }, [dispatch, tourId]);

  const { selectedDate, selectedTime } = useSelector((state) => state.datepicker);

  const handleClick = () => {
    if (onNext) onNext();
    navigate(nextLink);
  };

  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string"
          ? error
          : error.message || JSON.stringify(error)}
      </p>
    );

  return (
    <section
      key={ticketData?.id}
      style={{
        // padding: "2em",
        // boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
        // border: "1.5px solid #333",
        // borderRadius: "1em",
      }}
    >
      <h2>Your Tickets Overview</h2>

      <section style={{ display: "flex", gap: "1.25em", alignItems: "center", flexWrap: "wrap" }}>
        <img
          src={ticketData.img}
          alt="Tour"
          style={{ width: "10em", height: "6.8em", borderRadius: "0.8em", objectFit: "cover" }}
        />
        <section style={{ display: "flex", flexDirection: "column", gap: "0.3em" }}>
          <h2>{ticketData.title}</h2>
          <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
            <Icon icon="system-uicons:calendar-month" color="#FA8B02"/>
            <h3>{selectedDate}</h3>
          </section>
          <section style={{ display: "flex", gap: "0.625em", alignItems: "center" }}>
            <Icon icon="system-uicons:clock" color="#FA8B02"/>
            <h3>{selectedTime}</h3>
          </section>
        </section>
      </section>

      <hr />

      <section>
        {/* Adult */}
        <section className="details">
          <section className="dest_price">
            <section className="pplAmount"><p>{setAdultValue}</p></section>
            <p style={{ color: "#333" }}>Adult (18+) (€{ticketData.adultPrice})</p>
          </section>
          <h3>€{setTotalAdultPrice}</h3>
        </section>

        {/* Child */}
        <section className="details">
          <section className="dest_price">
            <section className="pplAmount"><p>{setChildValue}</p></section>
            <p style={{ color: "#333" }}>Child (6-17) (€{ticketData.childPrice})</p>
          </section>
          <h3>€{setTotalChildPrice}</h3>
        </section>

        {/* Infant */}
        <section className="details">
          <section className="dest_price">
            <section className="pplAmount"><p>{setInfantValue}</p></section>
            <p style={{ color: "#333" }}>Infant (0-5) (€{ticketData.infantPrice})</p>
          </section>
          <h3>€{setTotalInfantPrice}</h3>
        </section>
      </section>

      <hr />

      <section className="details">
        <h2 style={{ color: "#333" }}>Total Price</h2>
        <h2 style={{ color: "#FA8B02" }}>€{setTotalPrice}</h2>
      </section>

      <button onClick={handleClick} className="general_btn">
        <h3>Go to the Next Step</h3>
      </button>
    </section>
  );
}
