import { createSlice } from "@reduxjs/toolkit";
import Tuscany from '../DB/Tuscany.json';

const initialState = {
    selectedTourType:'PublicTour',
    selectedDate :null,
    selectedTime:null,
    specificTour:null,
    tours:Tuscany,
    tour:[]
};

const tourSlice = createSlice({
    name:'tour',
    initialState,
    // reducers:{
    //     setTourType: (state, action) => {
    //         state.selectedTourType = action.payload;
    //     },
        
    //     setDate:(state,action) => {
    //         state.selectedDate = action.payload;
    //     },
    //     setTime:(state,action) => {
    //         state.selectedTime  = action.payload;
    //     },
       
        
    // }
  setSpecificTour:(state,action) => {
    const foundTour = state.tours.find((tour)=> tour.id === action.payload);
    if(foundTour) {
        state.specificTour = foundTour;
    }else{
        console.log(Error,"Tour not found");
    }
  }
})

export const {setSpecificTour} = tourSlice.actions;
export default tourSlice.reducer;
