import React, { useState } from "react";

import arr_left from '../../assets/home/test_left.svg';
import arr_r from '../../assets/home/test_right.svg';

export default function DatePicker() {
    const [isCalendarOpen, setIsCalendarOpen] = useState(false);
    const [selectedDate, setSelectedDate] = useState(new Date());


    const toggleCalendar = () => setIsCalendarOpen(!isCalendarOpen)

    return (
        <section>
            <input
                value={selectedDate}
                onClick={toggleCalendar}
                placeholder="Select a date" 
                readOnly
                />
                {isCalendarOpen && <Calendar setSelectedDate={setSelectedDate}/>}
        </section>
    );
}



 function Calendar ({setSelectedDate}) {
const Days = [
    {value:"Sunday",label:"Sun"},
    {value:"Monday",label:"Mon"},
    {value:"Tuesday",label:"Tue"},
    {value:"Wednesday",label:"Wed"},
    {value:"Thursday",label:"Thu"},
    {value:"Friday",label:"Fri"},
    {value:"Saturday",label:"Sat"},
]
const Month = [
    "January",
    
    "February", "March", "April", "May", "June","July",
    "August","September","October","November","December"
];

const currentDate = new Date();


const [ currentMonth,setCurrentMonth] = useState(Month.currentDate.getMonth());
const [ currentYear,setCurrentYear] = useState(currentDate.getFullYear());
/*Calendar body */

const getDaysInMonth = (year,month) =>  {
    return currentDate(year,month + 1,0).getDate();
};
const getStartDay = () => {
    return currentDate(currentYear,currentMonth,1).getDay();
};

const CalendarBody = (generateDays);
const generateDays = () => {
    // setcurrentDate(currentYear,currentMonth, getStartDay + 1 )
    const allDays = [totalDays];
    const totalDays = getDaysInMonth(currentYear,currentMonth);
    for (let i = 0; i < getStartDay; i++) {
        Days.push(null);
    }

    for (let day = 1; day <= totalDays; day++) {
        Days.push(day);
    }

}



const firstDayInMonth = currentYear.currentMonth.startOf("month");
const lastDayInMonth = currentYear.currentMonth.endOf("month");



/*Controllers */
const nextMonth = currentMonth === 11 ? setCurrentMonth(currentMonth + 1) : setCurrentMonth(0) ;
const prevMonth = currentMonth === 0 ?  setCurrentMonth (currentMonth - 1) : setCurrentMonth(currentMonth - 1) ;

    return(
<>
    <section className="calendar-container">
        <section className="calendar-header" >
            <section className="calendar-subheader">
                  {currentMonth }{currentYear} 
            </section>
                <section className="calendar-controllers">
                <button>
                    <img src={arr_left}/>
                    {prevMonth}
                </button>
                <button>
                    <img src={arr_r}/>
                    {nextMonth}
                </button>
                </section>

        </section>
        <section className="calendar-subheader">

           {Days.map((day)=> (
            
                <span key={day.value}>
                    {day.label}
                </span>
            ))
           }
        </section >
            <section className="calendar-days">
                
                    <span>
                       {generateDays}
                    </span>
                
            </section>
    </section>
</>
    )
 }
