import React  from 'react';
import { useParams,Link  } from 'react-router-dom';
import tour from '../DB/Tuscany.json';
import BookingDetails from './Purchase/BookingDetails';
import Arrows from '../components/Carousel/Arrow';

import group from '../assets/home/icons/Tours/group_col.svg';
import duration from '../assets/home/icons/Tours/duration.svg';
import location from '../assets/home/icons/Footer/location.svg';
import guide from '../assets/home/icons/guide.svg';
import lang from '../assets/home/icons/Tours/language.svg';
import fees from '../assets/home/icons/Tours/ticket.svg';
import transport from '../assets/home/icons/Tours/transport.svg';
import { MainGallery } from '../components/MainGallery';
import { GalleryPlaces } from '../components/GalleryPlaces';

export default function SpecificTour() {


  return (
    // const { id } = useParams(); 
    // const tours = tour.find((tour) => gallery.id === parseInt(id));

    <section className='specificTour_main_container'>
      <section className="tour" key={tour.id}>
        <h2>{tour.title}</h2> 
        <p> from </p> <p><strong>Price:</strong> {tour.price} â‚¬</p>
      <section>

      </section>
        <MainGallery/>
        <p>{tour.short_description}</p>
        <section>
      
         <Link to={<BookingDetails/>}>
        <button> Buy Now</button>
        </Link>
         
       <h3>Details</h3>
        <p>{tour.details}</p>
        <img src={group} alt='number people of the group' /> <h3> Number of group: {tour.people}</h3>
        <img src={duration} alt='trip duration'/> <h3><strong>Duration:</strong> {tour.duration}</h3>   
        <img src={location } alt='departure area'/> <h3> Departuring and arriving areas: {tour.departure}</h3>
        <img src={guide} alt='guide'/> <h3> Guide service:{tour.guide}</h3>
        <img src={lang} alt='language tour'/> <h3>Language:{tour.language}</h3>
        <img src={fees} alt='entry fee'/> <h3>Entry Fees:{tour.fees} </h3>
        <img src={transport} alt='avaliable transport'/>  <h3> EntryTransportation:{tour.transport}</h3>
      </section>
    
      </section>
<GalleryPlaces/>
  
    </section>
  );
}
