import React from 'react';
import { useParams } from 'react-router-dom';
import tourData from '../DB/Tuscany.json';
import TuscanySlider from '../components/Carousel/TuscanyCarousel';

export default function SpecificTour() {
  const { id } = useParams(); 

  if (!tourData || tourData.length === 0) {
    return <p>Loading tours...</p>;
  }

  const tour = tourData.find((tour) => tour.id === parseInt(id));

  if (!tour) {
    return <p>Tour not found.</p>;
  }

  const { title, img, short_description,guide,departure,people, details, price,language, duration,fees,transport } = tour;

  return (
    <section>
      <section className="tour" key={tour.id}>
        <h2>{title}</h2> 
        <p> from </p><p><strong>Price:</strong> {price} â‚¬</p>

        <img src={img} alt={title} />
        <p>{short_description}</p>
        <section>
        <Datepicker/>
        <TimePicker/>
        <button></button>

       <h3>Details</h3>
        <p>{details}</p>
        <img/> <h3> Number of group: {people}</h3>
        <img/> <h3><strong>Duration:</strong> {duration}</h3>   
        <img/> <h3> Departuring and arriving areas: {departure}</h3>
        <img/> <h3> Guide service:{guide}</h3>
        <img/> <h3>Language:{language}</h3>
        <img/> <h3>Entry Fees:{fees} </h3>
        <img/>  <h3> EntryTransportation:{transport}</h3>
             </section>

      </section>

    {/* <TuscanySlider/> */}
    </section>
  );
}

