import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    value:0,
    increase:null,
    decrease:null,

    adultPrice:0,
    childPrice:0,
    infant:0,
    adultCost:0,
    childCost:0,
    totalCost:0,
}

const counterSlice = createSlice({
    name:'counter',
    initialState,
    reducers:{
        setIncrease :(state) => {
            state.value += 1;
        },
        setDescrease:(state) => {
            state.value -= 1;
        },
        setAdultCost : (state,action) => {
            state.adultPrice = action.payload;
            state.adultCost = state.value * state.adultPrice
        },
        setChildCost :(state,action) => {
            state.childPrice = action.payload;
            state.childCost = state.value * state.childPrice;
        },
        setInfantCost :(state,action) => {
            state.infantPrice = action.payload;
            state.infantCost = state.value * state.infantPrice;
        },
        setTotalCost :(state)=> {
                state.totalCost = state.AdultCost+state.ChildCost+state.InfantCost
        }

    }
})

export const {setIncrease,setDescrease,setAdultCost,setChildCost,setInfantCost,setTotalCost} = counterSlice.actions;
export default counterSlice.reducer;
