import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchTourById } from "../../slices/tourById";
import ProgressBar from '../../components/ProgressBar';
import TicketOverview from '../../components/TicketOverview';
import { Icon } from "@iconify/react";

import { 
  setAdultValue,
  increaseAdultValue,
  descreaseAdultValue,

  setChildValue,
  increaseChildValue,
  decreaseChildValue,

  setInfantValue,
  increaseInfantValue,
  descreaseInfantValue,
} from "../../slices/tourById";

import { selectedTime,selectedDate } from "../../slices/dateSlice";


export default function BookingDetails() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { ticketData, loading, error } = useSelector((state) => state.tour);

  const adultValue = useSelector(state => state.tour.adultValue);
  const childValue = useSelector(state => state.tour.childValue);
  const infantValue = useSelector(state => state.tour.infantValue);

  useEffect(() => {
    if (id) {
      dispatch(fetchTourById(id));
    }
  }, [dispatch, id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message || error}</p>;
  if (!ticketData) return <p>No data</p>;

  return (
    <section key={ticketData.id} className="tickets_body">
      <TicketCounter
        label="Adult (18+)"
        price={ticketData.adultPrice}
        value={adultValue}
        increase={() => dispatch(increaseAdultValue(1))}
        decrease={() => dispatch(descreaseAdultValue(1))}
        setValue={(val) => dispatch(setAdultValue(val))}
      />
      <TicketCounter
        label="Child (6-17)"
        price={ticketData.childPrice}
        value={childValue}
        increase={() => dispatch(increaseChildValue(1))}
        decrease={() => dispatch(decreaseChildValue(1))}
        setValue={(val) => dispatch(setChildValue(val))}
        info="With valid ID. Only in combination with: Adult (18+)"
      />
      <TicketCounter
        label="Infant (0-5)"
        price={ticketData.infantPrice}
        value={infantValue}
        increase={() => dispatch(increaseInfantValue(1))}
        decrease={() => dispatch(descreaseInfantValue(1))}
        setValue={(val) => dispatch(setInfantValue(val))}
        info="Only in combination with: Adult (18+)"
        freeLabel="FREE"
      />
    </section>
  );
}


const TicketCounter = ({ label, price, value, increase, decrease, setValue, info, freeLabel }) => (
  <section className="tickets_order">
    <ul>
      <h2>{label}</h2>
      {info && <li>{info}</li>}
      <h3 style={{ color: "#FA8B02" }}>
        â‚¬{price !== null && price !== undefined ? price : freeLabel || "Not available"}
      </h3>
    </ul>
    <section className="counter">
      <button onClick={decrease} disabled={value <= 0}>
        -
      </button>
      <input
        type="number"
        min={0}
        value={value}
        onChange={(e) => {
          const val = Math.max(0, Number(e.target.value));
          setValue(val);
        }}
      />
      <button onClick={increase}>+</button>
    </section>
  </section>
)
