import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    increase:null,
    decrease:null,

    adultPrice:0,
    childPrice:0,
    infant:0,
    adultCost:0,
    childCost:0,
}

const counterSlice = createSlice({
    name:'counter',
    initialState,
    reducers:{
        setIncrease :(state) => {
            state.value -= 1;
        },
        setDescrease:(state) => {
            state.value += 1;
        },
        setAdultPrice : (state,action) => {
            state.adultPrice = action.payload;
            state.adultCost = state.value * action.payload;
        },
        setChildCost :(state,action) => {
            state.childPrice = action.payload;
            state.childCost = state.value * action.payload;
        },
        setInfant

    }
})

export const {setIncrease,setDescrease,setAdultPrice} = counterSlice.actions;
export default counterSlice.reducer;
