import React, { useState } from "react";

import arr_left from '../../assets/home/test_left.svg';
import arr_r from '../../assets/home/test_right.svg';

export default function DatePicker() {
    const [isCalendarOpen, setIsCalendarOpen] = useState(false);
    const [selectedDate, setSelectedDate] = useState("");


    const toggleCalendar = () => setIsCalendarOpen(!isCalendarOpen)

    return (
        <section>
            <input
                value={selectedDate}
                onClick={toggleCalendar}
                placeholder="Select a date" 
                readOnly
                />
                {isCalendarOpen && <Calendar setSelectedDate={setSelectedDate}/>}
        </section>
    );
}



 function Calendar ({setSelectedDate}) {
const Days = [
    {value:"Sunday",label:"Sun"},
    {value:"Monday",label:"Mon"},
    {value:"Tuesday",label:"Tue"},
    {value:"Wednesday",label:"Wed"},
    {value:"Thursday",label:"Thu"},
    {value:"Friday",label:"Fri"},
    {value:"Saturday",label:"Sat"},
]

const currentDate = new Date();

const currentMonth = currentDate.getMonth();
const nextMonth = currentMonth + 1;
const prevMonth = currentMonth.getFullYear - 1;

const currentYear = currentDate.getFullYear();

const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    const calendarDays = Array.from({ length: daysInMonth }, (_, i) => i + 1);

    const handleDateClick = (day) => {
        const formattedDate = `${currentYear}-${currentMonth + 1}-${day}`;
        setSelectedDate(formattedDate);
    };

    return(
<>
    <section className="calendar-container">
        <section className="calendar-header" >
            <section className="calendar-subheader">
                  {currentMonth + 1}{currentYear} 
            </section>
                <section className="calendar-controllers">
                <button>
                    <img src={arr_left}/>
                </button>
                <button>
                    <img src={arr_r}/>
                </button>
                </section>

        </section>
        <section className="calendar-subheader">

           {Days.map((day)=> (
            
            <span key={day.value}>{day.label}</span>))}
        </section >
            <section className="calendar-days">
                {calendarDays.map((day)=> {
                    <span
                    key={day}
                    onClick={()=> handleDateClick(day)}
                    >
                        {day}
                    </span>
                })}
            </section>
    </section>
</>
    )
 }
