import React, { useState } from "react";
import {Link} from 'react-router-dom';
import ppl from "../assets/home/icons/Tours/groups.svg";
import clndr from "../assets/home/icons/Tours/date.svg";
import time from "../assets/home/icons/Tours/time.svg";
import tour from "../assets/home/icons/Tours/tour.svg";
import tran from "../assets/home/icons/Tours/transport.svg";
import search from "../assets/home/icons/Tours/zoom.svg";
import DatePicker from "../Picker/DatePicker.jsx";
import TimePicker from '../Picker/TimePicker.jsx'
import PubTours from '../assets/home/icons/Tours/publicTours.svg';
import PrivTours from '../assets/home/icons/Tours/privateTours.svg';

import Tuscany from  '../DB/Tuscany.json';
import Search from '../components/search.jsx'
import { useDispatch, useSelector } from "react-redux";

// import {useDispatch,useSelector} from 'react-redux'
// import { setTourType,setTime,setDate,setTour } from "../slices/tourSlice.js";


export default function TourPanel() {

const [open, setOpen] = useState(false);
const dispatch = useDispatch();
const selectedDate = useSelector((state)=> state.datepicker.selectedDate);

  const handleDateChange = (date) => {
    dispatch(selectedDate(date));
    setOpen(false);
  }
  const handleChange = (e) => {
    const selected = new Date(e.target.value);
    dispatch(selectedDate(selected));
  }
const findTour = Tuscany.map((tour)=> tour.title);
// const handleTourTypeChange

//transport
// const transportsBus = Tuscany.filter((tran)=> tran === 'Minivan and Bus');
// const transportNCC = Tuscany.filter((tran)=> tran === 'Transfers & NCC');
// const transportLuxury = Tuscany.filter((tran)=> tran ==='Luxury Experience');
const transportsBus = Tuscany.find(
  (tour) => tour.transport === 'Minivan and Bus'
);

const transportNCC = Tuscany.find(
  (tour) => tour.transport === 'Transfers & NCC'
);

const transportLuxury = Tuscany.find(
  (tour) => tour.transport === 'Luxury Experience'
);

//numberOfPeople
const numberOfPeople = [
  Tuscany.find((tour) => tour.people === '5-10'),
  Tuscany.find((tour) => tour.people === '5-12'),
  Tuscany.find((tour) => tour.people === '10-50'),
];

    return(
    <section className="main">
    <section className="btns">
      <section className="btns_tour ">     
        <button  className="public">
          <img src={PubTours} alt="Choose a public tour"/> 
          Public Tours
        </button>
      </section>
      <section className="btns_tour">
        <button  className="private ">
          <img src={PrivTours} alt="choose a private tour"/>
           Private Tours
        </button>
      </section>
    </section>
    <section className="tour_panel">
      
<section className="publicTour">
          <section className="search-container">

            <section className="searchItem">
                  <img src={ppl} alt="Choose a  number of  tour's people" />
                <section className="custom_container">
                    <span>Number of people</span>
                    <select>
                      <option>Choose number</option>
                        {numberOfPeople.map((people,index) => (
                          <option key={index} value={people.people}>
                            {people.people}
                          </option>
                      ))}
                     </select>
                 </section>
              </section> 

              <section  className="searchItem">
                <img src={clndr} alt="choose a tour's date"/>
                  <section className="custom_container">                      
                    <span>Date</span>
                      <select>
                        <option>
                          <span>Selected Date</span>
                        </option>
                      <option>
                        <section>
                          {/* <div onClick={() => setOpen(!open)} className="custom_select">
                        {selectedDate ? new Date(selectedDate).toDateString() : "Choose date"}
                      </div> */}
                      {open && (
                        <div >
                          <DatePicker onChange={handleDateChange} />
                        </div>
                      )}  
                        </section>
                      </option>
                      </select>
                      </section>
              </section>

              <section className="searchItem">
                        <TimePicker/>
              </section>
                        {/*Select Tour */}
              <section className="searchItem">
                <img src={tour} alt="choose a specific tour" />
                <section className="custom_container">
                <span>Tour</span>
                  <select>
                    {findTour.map((tour, index) => (
                    <option key={index} value={tour}>
                      {tour}
                    </option>
                  ))}
                  </select>
                  </section>
              </section>

              <section className="searchItem">
                <img src={tran} alt="choose a tour's transport" />
                <section className="custom_container">
                  <span>Transportation</span>
                    <select>
                      <option value={transportsBus.transport}>{transportsBus.transport}</option>
                      <option value={transportNCC.transport}>{transportNCC.transport}</option>
                      <option value={transportLuxury.transport}>{transportLuxury.transport}</option>
                    </select>
                  </section>
              </section>   
                        <Link to="/search" element={<Search/>}>
                        <button className="general_btn">
                                <img src={search} alt="search your tours"/>
                            </button>
                        </Link>
          </section>
      </section>
    
    </section>
    </section>
    )
}
