import React from 'react';
import { useParams } from 'react-router-dom';
import tourData from '../DB/Tuscany.json';
import gallery from '../DB/Gallery.json'

export default function SpecificTour() {
  const { id } = useParams(); // Get id from URL params

  // Handle case where tour data is still loading or is empty
  if (!tourData || tourData.length === 0) {
    return <p>Loading tours...</p>;
  }

  // Find the specific tour based on the ID from the URL
  const tour = tourData.find((tour) => tour.id === parseInt(id));

  // Handle the case where no tour matches the ID
  if (!tour) {
    return <p>Tour not found.</p>;
  }

  // Destructure the properties from the found tour
  const { title, img, short_description, details, price, duration } = tour;

  return (
    <section>
      <section className="tour" key={tour.id}>
        <h2>{title}</h2>
        <img src={img} alt={title} />
        <p>{short_description}</p>
        <p>{details}</p>
        <p><strong>Price:</strong> {price} â‚¬</p>
        <p><strong>Duration:</strong> {duration}</p>
      </section>

    <Gallery/>
    </section>
  );
}

