import React from 'react';
import { Link } from 'react-router-dom';
// import Dropdown from 'react-dropdown';
import logo from '../assets/home/logo.svg';
import avatar from '../assets/home/icons/CheckIn/avatar.svg';
import tickets from '../assets/home/icons/Tours/ticket.svg';
import logOut from '../assets/home/icons/CheckIn/logout.svg';
import { useDispatch, useSelector } from 'react-redux';

export default function Header() {
    // const [isModalOpen, setIsModalOpen] = useState(false);
 

    // const handleLanguageChange = (option) => {
    //     console.log(option.value); // Обробка зміни мови
    // };

    // const handleOpenModal = useCallback(() => {
    //     setIsModalOpen(true);
    // }, []);

    // const handleCloseModal = useCallback(() => {
    //     setIsModalOpen(false);
    // }, []);

const dispatch = useDispatch();
const {currentUser} = useSelector((state)=> state.auth.currentUser);
const {isNewUser} = useSelector((state)=> state.a)

    return (
        <header>
            <Link to='/'>
                <img className="logo" src={logo} alt="Tours to Tuscany" />
            </Link>

            <nav className="setUp">
                
                    <li>
                        <Link to='/'>Home</Link>
                    </li>
                    <li>
                        <Link to='/aboutUs' >About us</Link>
                    </li>
                    <li>
                        <Link to='/Tours'>Tours</Link>
                    </li>
                    <li>
                        <Link to='/contact' >Contact Us</Link>
                    </li>
                
            </nav>

            <section className="options">
                <select>
                    <option>
                        ENG
                    </option>
                    <option>
                        UKR
                    </option>
                </select>
                <Link to='/auth/login'>
                    <span>
                        Login
                    </span>
                </Link>
                {currentUser ? (
      <section className="custom_container">
        <button>
          <img src={avatar} alt="User avatar" />
        </button>

        <div className="dropdown_menu">
          {isNewUser && (
            <Link to="/account/tickets" className="dropdown_item">
              <img src={tickets} alt="tickets" />
              <span>My Tickets</span>
            </Link>
          )}
          <button className="dropdown_item" onClick={() => dispatch(logOut())}>
            <img src={logOut} alt="logout" />
            <span>Logout</span>
          </button>
        </div>
      </section>
    ) : (
      <Link to="/auth/createAccount">
        <button className="general_btn">Sign Up</button>
      </Link>
    )}
  
               
               
          </section>  
          

            {/* Рендеринг модального вікна */}
            {/* <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
                <CreateAccount />
            </Modal> */}
        </header>
    );
}
