import React, { useEffect } from 'react';
import {useParams} from 'react-router-dom';
import tourData from '../DB/Tuscany.json';


export default function specificTour() {

  if (!tour || tour.length === 0) {
    return <p>Loading tours...</p>;
  }

const {id} = useParams();

const tour = tourData.find((tour) => tour.id === parseInt(id));
const {title, img, short_description, details,price,duration} = tour;

  return (
  <section>
    
    <section className="tour" key={tour.id}>
        <h2>{tour.title}</h2>
        <img src={tour.img} alt={tour.title} />
        <p>{tour.short_description}</p>
        <p>{tour.details}</p>
        <p><strong></strong> {tour.price}</p>
        <p><strong></strong> {tour.duration}</p>
    </section>
  
  </section>
   
      
   
  );
}
