import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import { fetchTourById } from "../../slices/tourSlice";
import { setDescrease,setIncrease } from "../../slices/counterSlice";

export default function BookingDetails() {
    const { id } = useParams(); // Отримання ID з URL
    const dispatch = useDispatch();
    useEffect (()=> {
        dispatch(fetchTourById(id));
    },[dispatch,id]);
//Fetch data from redux store
    // const {specificTour,loading,error} = useSelector((state)=> state.tours);
    //counter
    const decrease = useSelector((state)=> state.counter.decrease);
    const increase = useSelector((state)=> state.counter.increase);

    //input
    const [value,setValue] = useState(0);

    const totalAdultCost = useSelector((state)=> state.counter.totalAdultCost);
    const totalChildCost = useSelector((state)=> state.counter.totalChildCost);

    if (loading) return <h3>Loading...</h3>;
    if (error) return <h3>Error: {error}</h3>;
    if (!specificTour) return <h3>Tour not found</h3>;

    return (
        <div>
            <img src={specificTour.img} alt={specificTour.title}/>
            <h2>{specificTour.title}</h2>
            <p>Price: {specificTour.adultPrice}€</p>
    <section>
                <button  
        onClick={() => dispatch(decrease())}> - </button>
      <input
        type="number"
        value={value} 
        onChange={(e)=> setValue(e.target.value)}
       />
      <button onClick={()=> dispatch(increase())}> + </button>
       <h6>Total Adult Cost: {dispatch(totalAdultCost)}</h6> 
       </section>
       <h6>Total Child Cost:{dispatch(totalChildCost)}</h6>

       <h3>{()=>setValue()}</h3>
                
        </div>
    );
}
