import React, { useState } from "react";
import ProgressBar from "../../components/ProgressBar";
import {Ticket} from './BookingDetails'
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { selectedDate, selectedTime } from "../../slices/dateSlice";
import TicketOverview from "../../components/TicketOverview";
export default function UserDetails () {
const fullName = useSelector((state)=> state.auth.fullName)
const email  = useSelector((state)=> state.auth.email);
const phone = useSelector((state)=> state.auth.phone);


    const handleSubmit = async(e) => {
        e.preventDefault()
    }
    return( 
        <>
        
    <ProgressBar currentStep={2}/>
    <section className="booking_container">
    
            <section className="user_details">        
            <h2>Who shall we send these tickets to?</h2>
            <form>
                <section className="user-container">
                   <section className="user_details">
                     <label htmlFor="username">Name</label>
                        <input 
                        name='username'
                        value={fullName}
                        onChange={(e)=> fullName(e.target.value)}
                        placeholder="Enter your name"/>
                   </section>
                    <section className="user_details">
                        <label htmlFor="surname">Surname</label>
                        <input
                         name="surname"
                         value={fullName}
                         onChange={(e)=> surname(e.target.value)} 
                         placeholder="Enter your surname"/>
                    </section>
                        
                    <section className="user_details">
                        <label htmlFor="phone">Telephone Number</label>
                        <input name='phone' 
                        type="tel"
                        value={phone}
                        onChange={(e)=> phone(e.target.value)}
                        placeholder="Enter your telephone number "/>
                    </section>
                       
                    <section className="user_details">
                        <label htmlFor='email'>Email Adress</label>
                        <input 
                        type="email"
                        value={email}
                        onChange={(e)=> email(e.target.value)}
                        name='email' placeholder="Enter your email adress"
                        />
                    </section>
                        
                </section>
            </form>
            </section>
                <TicketOverview
                nextLink={'/Payment'}
                />
    </section>
                        </>
    )
}
