},
decreaseInfantValue: (state) => {
  if (state.ticketData.infantValue > 0) state.ticketData.infantValue -= 1;
},
setImgTour: (state, action) => {
  const tour = Tuscany.find((tour) => tour.id === action.payload);
  state.ticketData.img = tour?.img || null;
},
setTourTitle: (state, action) => {
  const tour = Tuscany.find((tour) => tour.id === action.payload);
  state.ticketData.title = tour?.title || null;
},
setNumberOfGroup: (state, action) => {
  const groups = Tuscany.find((people) => people.id === action.payload);
  state.ticketData.numberOfGroup = groups?.numberOfGroup || null;
},
setTourDuration: (state, action) => {
  const durationTour = Tuscany.find((tour) => tour.id === action.payload);
  state.ticketData.duration = durationTour?.duration || null;
},
setGuide: (state, action) => {
  const guideInclude = Tuscany.find((guide) => guide.id === action.payload);
  state.ticketData.guide = guideInclude?.guide || null;
},
setLanguage: (state, action) => {
  const languageTour = Tuscany.find(
    (language) => language.id === action.payload
  );
  state.ticketData.language = languageTour?.language || null;
},
setTourFees: (state, action) => {
  const fees = Tuscany.find((tourFees) => tourFees.id === action.payload);
  state.ticketData.fees = fees?.fees || null;
},
setTourTransport: (state, action) => {
  state.ticketData.transport = action.payload || null;
},
setPaymentMethod: (state, action) => {
  state.ticketData.payment = action.payload;
},
setStatusTour: (state, action) => {
  const selectedDate = new Date(action.payload);
  const today = new Date();
  state.ticketData.statusTour = selectedDate > today ? "upcoming" : "ended";
},

