import React, { useRef, useState } from "react";

import { MapContainer,TileLayer } from 'react-leaflet';


import loc from '../assets/home/icons/location.svg';
import phone from '../assets/home/icons/phone.svg';
import mail from '../assets/home/icons/mail.svg';
import { Marker, Popup } from "leaflet";

export default function ContactUs() {
  const mapRef = useRef(null);

  const places = [
    {
      title: "Your Company Name", // Replace with your company name
      location: "Your Location", // Replace with your location details
      position: {
        lat: 51.505, // Replace with your latitude
        lng: -0.09, // Replace with your longitude
      },
    },
  ];

  return (
    <section>
      <section className="get_in_touch">
        {/* ... rest of your contact information */}
      </section>
      <section className="forForm">
        {/* ... rest of your form */}
      </section>

      <div id="map">
        <MapContainer center={places[0].position} zoom={13} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker key={places[0].title} position={places[0].position}>
            <Popup>
              {places[0].title} - {places[0].location}
            </Popup>
          </Marker>
        </MapContainer>
      </div>
    </section>
  );
}
