import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    increase:null,
    decrease:null,
    adultPrice:0,
    childPrice:0,
    infant:0
}

const counterSlice = createSlice({
    name:'counter',
    initialState,
    reducers:{
        setIncrease :(state) => {
            state.value -= 1;
            state.adultCost = state.value * state.adultPrice;
        },
        setDescrease:(state) => {
            state.value += 1;
            state.adultCost = state.value * state.adultPrice;
        },
        
    }
})

export const {setIncrease,setDescrease} = counterSlice.actions;
export default counterSlice.reducer;
