import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setDate } from "../";

export default function Datepicker() {
  const dispatch = useDispatch();
  const selectedDate = useSelector((state) => state.datepicker.SelectedDate);
  const [isOpen, setIsOpen] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const toggleCalendar = () => setIsOpen(!isOpen);

  const handleDateClick = (date) => {
    dispatch(setDate(date));
    setIsOpen(false);
  };

  const generateCalendarDays = () => {
    const firstDay = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
    const lastDay = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 0);
    
    let days = [];
    for (let i = 1; i <= lastDay.getDate(); i++) {
      days.push(new Date(currentMonth.getFullYear(), currentMonth.getMonth(), i));
    }
    
    return days;
  };

  return (
    <div className="datepicker">
      <button onClick={toggleCalendar}>
        {selectedDate ? selectedDate.toDateString() : "Select a date"}
      </button>

      {isOpen && (
        <div className="calendar">
          <div className="calendar-header">
            <button onClick={() => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1))}>&lt;</button>
            <span>{currentMonth.toLocaleString("default", { month: "long", year: "numeric" })}</span>
            <button onClick={() => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1))}>&gt;</button>
          </div>

          <div className="calendar-days">
            {generateCalendarDays().map((date) => (
              <button
                key={date}
                onClick={() => handleDateClick(date)}
                className={selectedDate && selectedDate.toDateString() === date.toDateString() ? "selected" : ""}
              >
                {date.getDate()}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
