import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

export const MainGallery = () => {
  const { id } = useParams();
  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#main-gallery", 
      children: "a",
      pswpModule: () => import("photoswipe"),
    });

    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0" ,className:"large"},
    { id: 1, src: gallery.img1, alt: "Gallery place 1" },
    { id: 2, src: gallery.img2, alt: "Gallery place 2",className:"full" },
    { id: 3, src: gallery.img3, alt: "Gallery place 3" },
  ];

  //  pswp-gallery

  return (
    <section className="carousel_container" id="main-gallery">
      {images.map((img) => (
        <a
          key={img.id}
          href={img.src}
          data-pswp-width={img.width || 800}
          data-pswp-height={img.height || 600}
          target="_blank"
          rel="noreferrer"
        >
          <section className="full">
            <img
              src={img.src}
              alt={img.alt}
              className={img.className || ""}
            />
          </section>
        </a>
      ))}
    </section>
  );
  
};
