import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ToursContext } from '../context/ToursContext'; // Import the context

export default function SpecificTour() {
  const { id } = useParams(); // Get the tour ID from the URL
  const { tours } = useContext(ToursContext); // Get the tours from context

  // If tours are not loaded yet
  if (!tours || tours.length === 0) {
    return <p>Loading tours...</p>;
  }

  // Find the specific tour based on the id
  const tour = tours.find(tour => parseInt(tour.id) === parseInt(id));

  if (!tour) {
    return <p>Tour not found.</p>; // If no tour matches the id
  }

  return (
    <section className="tour">
      <h2>{tour.title}</h2>
      <img src={tour.img} alt={tour.title} />
      <p>{tour.short_description}</p>
      <p>{tour.details}</p>
      <p><strong>Price:</strong> ${tour.price}</p>
      <p><strong>Duration:</strong> {tour.duration}</p>
    </section>
  );
}
