import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// AsyncThunk to fetch one tour by ID
export const fetchTourById = createAsyncThunk(
  "tour/fetchTourById",
  async (id, { rejectWithValue }) => {
    try {
      const response = await axios.get(`http://localhost:5000/tour/tours/${id}`);
      console.log("Server response DATA:", response.data);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response?.data || "Something went wrong");
    }
  }
);

const tourSlice = createSlice({
  name: "tour",
  initialState: {
    ticketData: null,
    loading: false,
    error: null,

    // Ticket counters
    adultValue: 0,
    childValue: 0,
    infantValue: 0,

    // Cost results
    adultCost: 0,
    childCost: 0,
    infantCost: 0,
    totalCost: 0,
  },
  reducers: {
    // Adult ticket
    setAdultValue: (state, action) => {
      state.adultValue = action.payload;
    },
    increaseAdultValue: (state, action) => {
      state.adultValue += action.payload;
    },
    decreaseAdultValue: (state, action) => {
      state.adultValue = Math.max(0, state.adultValue - action.payload);
    },
    setAdultCost: (state) => {
      state.adultCost = (state.ticketData?.adultPrice || 0) * state.adultValue;
    },

    // Child ticket
    setChildValue: (state, action) => {
      state.childValue = action.payload;
    },
    increaseChildValue: (state, action) => {
      state.childValue += action.payload;
    },
    decreaseChildValue: (state, action) => {
      state.childValue = Math.max(0, state.childValue - action.payload);
    },
    setChildCost: (state) => {
      state.childCost = (state.ticketData?.childPrice || 0) * state.childValue;
    },

    // Infant ticket
    setInfantValue: (state, action) => {
      state.infantValue = action.payload;
    },
    increaseInfantValue: (state, action) => {
      state.infantValue += action.payload;
    },
    decreaseInfantValue: (state, action) => {
      state.infantValue = Math.max(0, state.infantValue - action.payload);
    },
    setInfantCost: (state) => {
      state.infantCost = (state.ticketData?.infant || 0) * state.infantValue;
    },

    // Total cost
    setTotalCost: (state) => {
      state.totalCost = state.adultCost + state.childCost + state.infantCost;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchTourById.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchTourById.fulfilled, (state, action) => {
        console.log("Fetched tour payload:", action.payload);
        state.ticketData = action.payload;
        state.loading = false;
      })
      .addCase(fetchTourById.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload || action.error?.message || "Error loading tour";
      });
  },
});

export default tourSlice.reducer;

export const {
  setAdultValue,
  increaseAdultValue,
  decreaseAdultValue,
  setAdultCost,

  setChildValue,
  increaseChildValue,
  decreaseChildValue,
  setChildCost,

  setInfantValue,
  increaseInfantValue,
  decreaseInfantValue,
  setInfantCost,

  setTotalCost,
} = tourSlice.actions;
