import React, { useRef, useState } from "react";

import {useForm} from 'react-hook-form';

import {MapContainer} from 'react-leaflet/MapContainer';
import {TileLayer} from 'react-leaflet/TileLayer';

import loc from '../assets/home/icons/location.svg';
import phone from '../assets/home/icons/phone.svg';
import mail from '../assets/home/icons/mail.svg';
import { Marker, Popup } from "leaflet";



export default  function ContactUs () {
const mapRef = useRef(null);


//MAP SECTION
//     const places = [
//     {
//         title:"Tuscany",
//         location:"Tuscany"
//         position:{
//             lat:43.56469,
//             lng:11.02011,
//         },
//         {
//             location:"Lucca",
//             position:{
//                 lat:43.84778,
//                 lng:10.51474,
//             }
//         },
//         {
//             location:"Cinque Terre",
//             position:{
//                 lat:44.12739,
//                 lng:9.11646,
//             },
//         },
//         {
//             location:"Sienna",
//             position:{
//                 lat:43.32015,
//                 lng:11.33231,
//             },
//         },
//         {
//             location:"Verona",
//             position:{
//                 lat:45.43931,
//                 lng:10.97976,
//             },
//         },
//         {
//             location:"Pisa",
//             position:{
//                 lat:43.72034,
//                 lng:10.40444,
//             },
//         },
//         {
//             location:"Florence",
//             position:{
//                 lat:43.77656,
//                 lng:11.26965
//             }
//         }
//     }
// ]

  
        return(
            <section> 
            <section className="get_in_touch">
                <section className="contact_touch" >
                    <h3>Get In Touch!</h3>
                    <h5> Fill up the form and our Team will get back to you within 24 hours.</h5>
                  <button> <img src={loc} alt="Piazza Napoleone"/> <p>Piazza Napoleone, Lucca, Tuscany</p></button>
                  <button> <img src={phone} alt="call to us"/> <p>	+39 346 368 5708</p></button>
                  <button> <img src={mail} alt="send to us"/> <p>	italiainlimo@gmail.com</p></button>
                </section>
                <section className="forForm">
                    <form  onSubmit={onSubmit}>
                        <label/> Name and Surname
                            <input placeholder="Enter your name and surname" {...register("Name and Surname")}/>
                            <label/>Email Address
                            <input placeholder="Enter your email address" {...register("Email")}/>
                            <label/> Message
                            <input type="textarea" placeholder="Enter your message"/>
                          
                     </form> 
                      <button className="btn_submit" type="submit" > <span>Send Message</span> </button> 
                 </section>
             <section className="interc_map">
                
               </section>
                
             </section>
             <div ref={mapRef}>
              <MapContainer>
              <TileLayer
               attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              >

              </TileLayer>
            </MapContainer>
             </div>
            
            </section>
    )
    
}
