import React, {  useContext, useEffect, useState } from "react";
import {Link} from 'react-router-dom'
import { useBook } from "../../context/BookContext";
import UserDetails from '../Purchase/UserDetails'
import { DateContext } from "../../context/DateContext";

// import ProgressBar from "../../components/ProgressBar";
export default function BookingDetails () {
 const {ticketData,setTicketData} = useBook();
 const {selectedDate,selectedTime} = useContext(DateContext);

    const [ticket, setTicket] = useState(0);

    const increaseTicket = () => setTicket((ticket) => ticket + 1);
    const decreaseTicket = () => setTicket((ticket) => (ticket > 0 ? ticket - 1 : 0));

   

    // useEffect (()=> {
    //     localStorage.setItem("ticketItem",JSON.stringify(ticketData));
    // },[ticketData])

    return(
        <> 
        <progress value={30} max={100}/>
<section>


        <section className="bookingDetails">
            {/* <section>
                <ProgressBar />
            </section> */}

            <section className="details">
                <label>When you visit?</label>
             <option>{selectedDate}</option> 
            </section>

            <section className="details">
                <label>Which time</label>
               
                       <option>{selectedTime}</option>
                  
            </section>
           

            <section className="details_tickets">
                <section className="tickets_info">
                    <ul><h2>Select Your Tickets</h2>
                        <li>Free for kids under 6 and disabled visitors (74%+) </li>
                        <li> Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue </li>
                    </ul>
                </section>

                <section className="tickets_order">
                    <section className="tickets_body">
                        <h2>Adult(18+)</h2>
                        {/* <h3>€{Tuscany.price}</h3> */}
                    </section>
                        <section className="tickets_counter">
                            <button
                            onClick={decreaseTicket}
                            >-</button>
                            <input 
                            value={ticket}
                            readOnly
                            placeholder="0" />
                            <button
                            onClick={increaseTicket}
                            >+</button>
                        </section>
                </section>

                <section className="tickets_order">
                    <section className="tickets_body">
                        <h2>Child(6-17)</h2>
                    <ul>
                        <li>With valid ID</li>
                        <li>Only in combination with: Adult (18+)</li>
                    </ul>
                    <h3>€{setTicketData.price}</h3>
                    </section>
                    
                    <section className="tickets_counter">
                        <button
                        onClick={decreaseTicket}
                        >-</button>
                        <input
                        value={ticket}
                        readOnly
                        placeholder="0" />
                        <button
                        onClick={increaseTicket}
                        >+</button>
                    </section>
                </section>

                <section className="tickets_order">
                <section className="tickets_body">
                    <h2>Infant(0-5)</h2>
                    <ul>
                        <li>Only in combination with: Adult (18+)</li>
                    </ul>
                    {/* <h3>€{Tuscany.price}</h3> */}
                    </section>
                    <section className="tickets_counter">
                        <button
                        onClick={decreaseTicket}
                        >-</button>
                        <input 
                        value={ticket}
                        readOnly
                        placeholder="0" />
                        <button
                        onClick={increaseTicket}
                        >+</button>
                    </section>
                </section>
            </section>

        
        </section>  

       <TicketOverview ticketItems={ticketData}/>
        </section>
        </>
    )
}
 function TicketOverview () {
    
    const {ticketItems} = useBook();

    useEffect (()=> {
        localStorage.setItem("ticketItem",JSON.stringify(ticketItems));
    },[ticketItems])
    return(
        <>
        <section className="tickets_overview">
              <h2>Your ticket Overview</h2>
              <section>
                  {/* {/* <img src={Tuscany.image} alt={Tuscany.title}/> */}
        {
            
        }  <section>
          <h2>title</h2> 
                  <p>{ticketItems.date}</p>
                  <p>{ticketItems.time}</p>
              </section>
          </section>
          <hr />
          <section className="quanity">
              <section>
                  {/* <label>{adult.quantity}</label>
          <p>Adult(18+){adult.amount}</p> */}
              </section>
              <section>
                  {/* <label>{child.quantity}</label>
          <p>Child(6-17){child.amount}</p> */}
              </section>
              <section>
                  {/* <label>{infant.quantity}</label>
          <p>Infant(0-5){infant.amount}</p> */}
              </section>
          </section>
          <hr />
          <section className="tckt_amount">
              {/* <p>{adult.amount}</p>
          <p>{child.amount}</p>
          <p>{infant.amount}</p> */}
          </section>
          <section>
              <h2>Total Price</h2>
              {/* <p>€{totalAmount}</p> */}
          </section>
          <Link to="/user" element={<UserDetails/>}>
         
          <button className="general_btn">Go to the Next Step</button> 
          </Link>
      </section>
      </>
    )
}
