import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

export const MainGallery = () => {
  const { id } = useParams();
  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#main-gallery", // Тепер правильний селектор
      children: "a",
      pswpModule: () => import("photoswipe"),
      // maxWidthToDisplay:800,
    });

    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0" },
    { id: 1, src: gallery.img1, alt: "Gallery place 1" },
    { id: 2, src: gallery.img2, alt: "Gallery place 2" },
    { id: 3, src: gallery.img3, alt: "Gallery place 3" },
  ];

  

  return (
    <section className="main_gallery pswp-gallery" id="main-gallery">
      {images.map((img) => (
        <a
          key={img.id}
          href={img.src}
          data-pswp-width={img.width || 800} 
          data-pswp-height={img.hei}
          target="_blank"
          rel="noreferrer"
        >
          <img src={img.src} alt={img.alt} />
        </a>
      ))}
    </section>
  );
};
