import React, { useContext, useState } from "react";
import {useDispatch,useSelector} from 'react-redux';
import {Link, useParams } from 'react-router-dom'
import ProgressBar from "../../components/ProgressBar";
import Tours from '../../DB/Tuscany.json';
import { setIncrease } from "../../slices/counterSlice";


export default function BookingDetails() {
    const {id} = useParams();

    const dispatch = useDispatch();

    const adultPriceAmount = useSelector((state) =>  state.counter.value); 
    const adultCost = useSelector((state)=> state.counter.adultCost);
    const childPriceAmount = useSelector((state)=> state.counter.va)

    const [value,setValue] = useState('');
 
    const handleChange = (e) =>{
     setValue(e.target.value);
    };
 

    return (
        <>
             <section className="booking_container">
                <ProgressBar/>
                <section className="booking_main">
                {/* Date*/}
                <section className="booking_details" >
                    <h2>When you will visit?</h2>
                   <select>
                    <option>
                        {toursData.selectedDay || "Choose a day"}
                    </option>
                   </select>
                </section>
                {/*Time */}
                <section className="booking_details" >
                    <label><h2>Which time?</h2></label>
                   <select>
                    <option>
                        {toursData.selectedTime || "choose the time" }
                    </option>
                   </select>
                   
                </section>
                    {/*Tickets */}
                    <ul>
                        <h2>Select Your Tickets</h2>
                        <li>Free for kids under 6 and disabled visitors (74%+) </li>
                        <li>Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue</li>
                    </ul>
                <section className="tickets_order">
                    <ul>
                        <h2>Adult(18+)</h2>
                        <h3>{Tours.price}</h3>
                    </ul>
                    <section className="tickets_counter">
             <button  
             onClick={() => dispatch(setDescrease())}>-</button>
                 <input
                     type="number"
                     value={value} 
                     onChange={handleChange}
                 />
             <button onClick={()=> dispatch(setIncrease())}>+</button>
             <h6>Total Adult Cost: {adultCost}</h6>
        </section>
                   
                </section>

                 <section className="tickets_order">
                    <ul >
                         <h2>Child (6-17)</h2>
                        <ul>
                            <li>With valid ID</li>
                            <li>Only in combination with: Adult (18+)</li>
                        </ul>
                    </ul>

                    <> 
                    
        
       
                    </>
                 
                </section>
               
                <section className="tickets_order">
                    <ul>
                        <h2>Infant (0-5) </h2>
                        <ul>
                            <li>Only in combination with: Adult (18+)</li>
                        </ul>
                    </ul>
                   
                    <>
                    </>
                </section> 
                </section> 
              <TicketOverview/>
            </section> 
        </>
    );
}



export const  TicketOverview = () => {
 

    return(
        <>
 <section>
            <h2>Your Ticket Overview</h2>
            <section >
                <img src={toursData.img} alt={toursData.title}/>
                <section>
                    <h2>{toursData.title}</h2>
                    <section>
                        {/* <img/> */}
                        <h3> {toursData.selectedDay || "Choose a day"}</h3>
                    </section>
                </section>
                <section>
                    {/* <img/> */}
                    <h3>  {toursData.selectedTime || "choose the time" }</h3>
                </section>
            </section>
<hr/>
            <section className="ticket_humans" >
                <ul>
                   
                    <section className="tickets_counter">
                        <section className="ticket_count"></section>  <p> Adult (18+)   </p> 
                    </section>
                               
                    <section className="tickets_counter">
                        <section className="ticket_count"></section>  <p> Child (6-17)   </p> 
                    </section>
                             

                    <section className="tickets_counter">
                        <section className="ticket_count"></section>  <p>Infant (0-5)  </p> 
                    </section>
                </ul>
                <ul>
                    <li><h3>total cost adult </h3></li>
                    <li><h3>total cost child </h3></li>
                    <li><h3>total cost infant</h3></li>
                </ul>

            </section>
            <hr/>
            <section>
                <h2>Total Price</h2>
                <h2 style={{color:'#f78b42'}}> {totalPrice}</h2>
            </section>
            <section>
                <Link to="/User" element={<UserDetails/>}>
                <button className="general_btn">
                    <h2>Go to the Next Step</h2>
                </button> 
                </Link>
            </section>
        </section>
        </>
    )
}

