export default function BookingDetails() {
  const { id } = useParams();
  const dispatch = useDispatch();

  const { ticketData, loading, error } = useSelector((state) => state.tour);

  useEffect(() => {
    dispatch(fetchTourById(id));
  }, [dispatch, id]);

  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string"
          ? error
          : error.message || JSON.stringify(error)}
      </p>
    );

  if (!ticketData) return <p>No data</p>;

  return (
    <div key={ticketData.id}>
      <h1>{ticketData.title}</h1>
      <img src={ticketData.img} alt={ticketData.title} />
      <p>Adult Price: {ticketData.adultPrice}</p>
      <p>Child Price: {ticketData.childPrice}</p>
      <p>Infant Price: {ticketData.infantPrice}</p>
    </div>
  );
}
