import React, { useState, useEffect, useContext } from "react";
import prev from '../../assets/home/icons/test_left.svg';
import next from '../../assets/home/icons/test_right.svg';
import { DateContext } from "../../context/DateContext";

export default function Calendar() {
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const months = ['January', 'February', 'March', 'April', 'May', 'June',
                  'July', 'August', 'September', 'October', 'November', 'December'];

  // State for the current date
  const [currentDay, setCurrentDay] = useState(new Date());
  const [currentDays, setCurrentDays] = useState([]);
  const {selectedDate,setSelectedDate} = useContext(DateContext);
  const handleDateChange =  

  // Generate the days for the calendar whenever currentDay changes
  useEffect(() => {
    const firstDayOfMonth = new Date(currentDay.getFullYear(), currentDay.getMonth(), 1);
    const weekdayOfFirstDay = firstDayOfMonth.getDay();
    const days = [];

    // Generate days for the calendar
    for (let day = 0; day < 42; day++) {
      // Adjust the first day of the month to the start of the week
      const calendarDate = new Date(firstDayOfMonth);
      calendarDate.setDate(calendarDate.getDate() - weekdayOfFirstDay + day);

      const calendarDay = {
        currentMonth: calendarDate.getMonth() === currentDay.getMonth(),
        date: calendarDate,
        month: calendarDate.getMonth(),
        selected: calendarDate.toDateString() === currentDay.toDateString(),
        number: calendarDate.getDate(),
        year: calendarDate.getFullYear()
      };

      days.push(calendarDay);
    }

    setCurrentDays(days);
  }, [currentDay]);

  // Handle previous and next month
  const handlePrevMonth = () => {
    setCurrentDay(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(newDate.getMonth() - 1);
      return newDate;
    });
  };

  const handleNextMonth = () => {
    setCurrentDay(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(newDate.getMonth() + 1);
      return newDate;
    });
  };
//selected currentDay color-orange, border-box, cursor:pointer
//avaliable days - color-as usual, unavaliable - #333333 + locked 
const changeDate = (e) => {
  setCurrentDay(e);
}
  return (
    <section className="calendar">
      <section className="calendar-header">
        <h5>{months[currentDay.getMonth()]} {currentDay.getFullYear()}</h5>
        <section className="calendar_buttons">
          <button onClick={handlePrevMonth}>
            <img src={prev} alt="Previous Month" />
          </button>
          <button onClick={handleNextMonth}>
            <img src={next} alt="Next Month" />
          </button>
        </section>
      </section>

      <section className="calendar-subheader">
        {daysOfWeek.map((day, index) => (
          <p key={index}>{day}</p>
        ))}
      </section>

      <section className="calendar-days">
        {currentDays.map((day, index) => (
          <section 
            key={index}
            className={"calendar-day " + 
              (day.currentMonth ? "current " : "") + 
              (day.selected ? "selected " : "")
            }
          >
            {day.number}
          </section>
        ))}
      </section>
    </section>
  );
}
