import React,{useEffect} from "react";
import { Link, useNavigate,useParams, } from "react-router-dom";
import { useSelector,useDispatch } from "react-redux";

import date from '../assets/home/icons/Tours/date.svg';
import time from '../assets/home/icons/Tours/time.svg';
import { fetchTourById } from "../slices/tourSlice";

export default function TicketOverview({ nextLink, onNext }) {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { ticketData, loading, error } = useSelector((state) => state.tour);

  useEffect(() => {
    dispatch(fetchTourById(id));
  }, [dispatch, id]);



  const adultValue = useSelector((state) => state.tour.adultValue);
  const childValue = useSelector((state) => state.tour.childValue);
  const infantValue = useSelector((state) => state.tour.infantValue);

  const adultPrice = useSelector((state) => state.tour.adultPrice);
  const childPrice = useSelector((state) => state.tour.childPrice);
  const infantPrice = useSelector((state) => state.tour.infantPrice);

  const totalCost = useSelector((state) => state.tour.totalCost);
  const adultCost = useSelector((state) => state.tour.adultCost);
  const childCost = useSelector((state) => state.tour.childCost);
  const infantCost = useSelector((state) => state.tour.infantCost);

  const selectedDate = useSelector((state) => state.datepicker.selectedDate);
  const selectedTime = useSelector((state) => state.datepicker.selectedTime);

  const tourTitle = useSelector((state) => state.tour.title);
  const tourImg = useSelector((state) => state.tour.img);
  
  if (loading) return <p>Loading...</p>;

  if (error)
    return (
      <p>
        Error:{" "}
        {typeof error === "string"
          ? error
          : error.message || JSON.stringify(error)}
      </p>
    );

  if (!ticketData) return <p>No data</p>;

  const navigate = useNavigate();
  const handleClick = () => {
    if (onNext)
      onNext()
    navigate(nextLink);
  }

  return (
    <section
      key={ticketData.id}
      style={{ padding: '2em', boxShadow: '#3333333', border: '1.5em solid #3333333' }}>
      <h2>Your Tickets Overview</h2>
      <section>
        <section style={{ display: "flex", gap: '1.25em', alignItems: 'center' }}>
          <img
            src={tourImg}
            alt="Tour's img"
            style={{ width: '10em', height: '6.8em', borderRadius: '0.8em' }}
          />
          <section style={{ display: 'flex', flexDirection: 'column' }}>
            <h2>{tourTitle}</h2>
            <section style={{ display: 'flex', gap: '0.625em' }}>
              <img src={date} alt="select a date" /><h3>{selectedDate}</h3>
            </section>
            <section style={{ display: 'flex', gap: '0.625em' }} >
              <img src={time} alt="select a time" /> <h3>{selectedTime}</h3>
            </section>
          </section>
        </section>
        <hr />
        <section>
          {/*Adult Peoples */}
          <section className="details"   >
            <section className="dest_price">
              <section className="pplAmount"><p>{adultValue}</p></section>
              <p style={{ color: '#333333' }}>Adult(18+) (€{ticketData.adultPrice} )</p>
            </section>
            <h3>€{adultCost}</h3>
          </section>
          {/*Child Peoples */}
          <section className="details">
            <section className="dest_price">
              <section className="pplAmount"><p>{childValue}</p></section>
              <p style={{ color: '#333333' }}>Child(6-17) (€{childPrice})</p>
            </section>
            <h3>€{childCost}</h3>
          </section>

          {/*Infant */}
          <section className="details">
            <section className="dest_price">
              <section className="pplAmount"><p>{infantValue}</p></section>
              <p style={{ color: '#333333' }}>Infant (0-5) (€{infantPrice})</p>
            </section>
            <h3>€{infantCost}</h3>
          </section>
        </section>
        <hr />

        <section className="details">
          <h2 style={{ color: '#333333' }}>Total Price</h2>
          <h2 style={{ color: "#FA8B02" }}>€{totalCost}</h2>
        </section>
        <Link to={nextLink}>
          <button
            onClick={handleClick}
            className="general_btn"><h3>Go to the Next Step</h3> </button>
        </Link>
      </section>
    </section>
  )
}
