import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import PhotoSwipeLightbox from 'photoswipe/lightbox';
import 'photoswipe/style.css'


export const MainGallery = () => {
 useEffect(()=> {
  let lightbox = new PhotoSwipeLightbox({
    gallery :'#' +images.id,
    children : 'img',
    pswpModule:()=> import('photoswipe'),
  });
  lightbox.init();
  return()=> {
    lightbox.destroy()
    lightbox = null;
  }
}) ;
const { id } = useParams();
  const [activeImage, setActiveImage] = useState(null); 
  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  // Перевірка, чи існує галерея
  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0" },
    { id: 1, src: gallery.img1, alt: "Gallery place 1" },
    { id: 2, src: gallery.img2, alt: "Gallery place 2" },
    { id: 3, src: gallery.img3, alt: "Gallery place 3" },
  ];

  const validImages = images.filter((img) => img.id >= 0 && img.id <= 4); 

  return (
    <>
     
    <section className="main_gallery pswp-gallery" key={gallery.id}>
        <img
          src={gallery.img}
          alt="Gallery Place"
          
        />
        <section className="main_gallery_items">
          <img
           src={gallery.img1}
            alt="Gallery Place 1"
            // data-pswp-width = {img.width}
            target = '_blank'
            rel="norefferrer"
             />
          <img src={gallery.img2} alt="Gallery Place 2"/>
          <img src={gallery.img3} alt="Gallery Place 3"/>
        </section>
      </section>
    

      {/* {activeImage !== null && (
        <Modal showModal={true} onClose={handleCloseModal}>
          <img
            src={validImages.find((img) => img.id === activeImage)?.src}
            alt="Selected Gallery Place"
          />
        </Modal>
      )} */}
    </>
  );
};
