import React, { useState } from "react";
import { useParams} from "react-router-dom";
import tour from '../DB/Tuscany.json';

export const MainGallery = () => {
    const { id } = useParams(); 
  
const [open,setOpen] = useState(false);   

    const gallery = tour.find((gallery) => gallery.id === parseInt(id));


    // Add a check to handle cases when gallery is undefined
    if (!gallery) {
        return <p>Gallery not found</p>;
    }
   


    return(
      
         <section className='main_gallery' key={gallery.id}>
          <img 
           src={gallery.img}
            alt="Gallery Image"
            onClose={()=> setOpen(true)}
            />  
          <section className="main_gallery_items">
            <img  
            src={gallery.img1} 
            alt="Gallery Image 1"
            onClose={()=> setOpen(true)}
            />
            <img 
            src={gallery.img2} 
            alt="Gallery Image 2" 
          
                />
            <img  
            src={gallery.img3} 
            alt="Gallery Image 3"
            onClose={()=> setOpen(true)}
            />
        </section>
           </section> 
          
    );
     
}
