import React, { useState, useEffect } from "react";
import Tuscany from '../DB/Tuscany.json';

export default function SpecificTour({ id }) {
  const [selectedTour, setSelectedTour] = useState(null);

  useEffect(() => {
    const foundTour = Tuscany.find(tour => tour.id === Number(id));
    setSelectedTour(foundTour);
  }, [id, Tuscany]);

  return (
    <section>
      {selectedTour ? (
        <div>
          <h2>{selectedTour.title}</h2>
          <p>{selectedTour.description}</p>
          {/* Other tour details */}
        </div>
      ) : (
        <p>Tour not found or loading...</p>
      )}
    </section>
  );
}
