import React, { useState } from "react";
import {Link, useNavigate } from 'react-router-dom'
import Login from "./Login";
import NoTickets from '../account/NoTickets.jsx';
import ggl from '../../assets/home/icons/CheckIn/google.svg'
import { useDispatch,useSelector } from "react-redux";
import { signUp } from "../../slices/authSlice.js";


export default  function CreateAccount () {


const dispatch = useDispatch();
const navigate = useNavigate();

//Стан керування станом даними
const [email,setEmail] = useState('');
const [password,setPassword] = useState('');
const [username,setUsername] = useState('');


const {user,error} = useSelector((state)=> state.auth);

const handleSubmit = async(e) => {

    e.preventDefault();
    //Відправка даних у Redux
    dispatch(signUp({email,password,username}));
   }
   //Механіз подальшої навігації
   if(user) {
    navigate('/account/noTickets')
   }

return(

   <section className="auth">
            <h1>Create Account</h1>
            <form 
                onSubmit={handleSubmit}
            >
                <label htmlFor="name">Name and Surname</label>
                    <input 
                        value={username}
                        name="name"  
                        placeholder="Enter your name and surname"
                        onChange={(e)=> setUsername(e.target.value)}
                    />

                <label htmlFor="email">Email Address</label>
                    <input
                        type="email"
                        name="email"
                        onChange={(e)=> setEmail(e.target.value)}
                        value={email}
                        placeholder="Enter your email address"
                    />
                <label htmlFor="password">Password</label>
                <input 
                    type="password"
                    name="password"
                    aria-describedby="password"
                    aria-invalid="false"
                    onChange={(e)=> setPassword(e.target.value)}
                    value={password}
                    placeholder="Enter your password"
                />

                <section className="checkbox">
                    <input required type="checkbox"/> 
                    <p>
                        I agree with <span className="highlight">Terms</span> and{" "}
                        <span className="highlight">Privacy</span>
                    </p>
                </section>
                
                    <section className="form" method='get'>
                            <button 
                            className="general_btn"
                             type="submit">
                          <span>Sign Up</span> </button>
                            <p>or</p>
                        <button 
                        className="btn_ggl_submit" 
                        type="submit"
                         placeholder="Sign Up with Google"> 
                            <img src={ggl} alt="signIn with Google"/> 
                            <span>Sign In with Google</span>  
                        </button>
                    <section className="additionals">
                        <p>Already have an account?</p>
                            <Link to='/auth/login'element={<Login/>}>
                                <button style={{color:"FA8B02"}}>Log In
                                </button>
                            </Link>
                    </section>
                    
                    </section>
            </form>
            {error && <p style={{color:"red"}}> {error}</p>}
    </section>

    )
};
