<>
   <section className="auth">
            <h1>Create Account</h1>
            <form 
            action="'/auth"
            method="POST"
            >
                <label>Name and Surname</label>
                    <input 
                        name="name"  
                        placeholder="Enter your name and surname"
                    />

                <label>Email Address</label>
                    <input
                        type="email"
                        name="email"
                        onChange={(e)=> setEmail(e.target.value)}
                        value={email}
                        placeholder="Enter your email address"
                    />
                <label>Password</label>
                <input 
                    type="password"
                    name="password"
                    aria-describedby="password"
                    aria-invalid="false"
                    onChange={(e)=> setPassword(e.target.value)}
                    value={password}
                    placeholder="Enter your password"
                />

                <section className="checkbox">
                    <input required type="checkbox"/> 
                    <p>I agree with <span className="highlight"> Terms </span>and <span className="highlight">Privacy</span></p>
                </section>
                
                    <section className="form" method='get'>
                            <button className="general_btn" type="submit" > <span>sign up</span> </button>
                            <p>or</p>
                        <button className="btn_ggl_submit" type="submit" placeholder="Sign Up with Google"> 
                            <img src={ggl} alt="signIn with Google"/> 
                            <span>Sign In with Google</span>  
                        </button>
                    <section className="additionals">
                        <p>Already have an account?</p>
                            <Link to='/auth/login'element={<Login/>}>
                                <button >Log In
                                    {/* {error && <span>{error}</span>} */}
                                </button>
                            </Link>
                    </section>
                    
                    </section>
            </form>
    </section>
</>
